package Ciclos;

public class Ciclo_do_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/* 
		 Ciclo do while
		 
		 do{
		 	instrucciones;
		 } while (condicion);		 
		 
		 */
		
		int i=1;
		
		do {
			System.out.println (i);
			i++;
		} while (i<=10);
		
		

	}

}
